import React, { useState, useCallback } from 'react';
import './App.css';

import Canvas from './components/Canvas';
import Controls from './components/Controls';
import Statistics from './components/Statistics';
import { computeLine as stepByStepLine } from './algorithms/stepByStep';
import { computeLine as ddaLine } from './algorithms/dda';
import { computeLine as bresenhamLineLine } from './algorithms/bresenhamLine';
import { computeCircle as bresenhamCircleFunc } from './algorithms/bresenhamCircle';

const ALGORITHMS = {
  stepByStep: stepByStepLine,
  dda: ddaLine,
  bresenhamLine: bresenhamLineLine,
  bresenhamCircle: bresenhamCircleFunc,
};

const LINE_ALGOS = ['stepByStep', 'dda', 'bresenhamLine'];

function App() {
  const [algorithm, setAlgorithm] = useState('stepByStep');
  const [mode, setMode] = useState('line');
  const [lineInputs, setLineInputs] = useState({x1: 0, y1: 0, x2: 10, y2: 10});
  const [circleInputs, setCircleInputs] = useState({xc: 0, yc: 0, r: 10});
  const [selectedPoints, setSelectedPoints] = useState([]);
  const [currentResult, setCurrentResult] = useState(null);
  const [allResults, setAllResults] = useState([]);
  const [results, setResults] = useState([]); // для canvas
  const [scale, setScale] = useState(25);

  const handleInputChange = useCallback((inputMode, field, value) => {
    if (inputMode === 'line') {
      setLineInputs(prev => ({...prev, [field]: value}));
    } else {
      setCircleInputs(prev => ({...prev, [field]: value}));
    }
  }, []);

  const handlePointSelect = useCallback((wx, wy) => {
    const newPoints = [...selectedPoints];
    if (newPoints.length >= 2) {
      newPoints.length = 0; // reset
    }
    newPoints.push([wx, wy]);
    setSelectedPoints(newPoints);

    // auto fill inputs
    if (mode === 'line' && newPoints.length === 2) {
      setLineInputs({
        x1: newPoints[0][0],
        y1: newPoints[0][1],
        x2: newPoints[1][0],
        y2: newPoints[1][1]
      });
    } else if (mode === 'circle' && newPoints.length === 2) {
      const dx = newPoints[1][0] - newPoints[0][0];
      const dy = newPoints[1][1] - newPoints[0][1];
      const r = Math.round(Math.sqrt(dx*dx + dy*dy));
      setCircleInputs({
        xc: newPoints[0][0],
        yc: newPoints[0][1],
        r
      });
    }
  }, [selectedPoints, mode]);

  const getParams = useCallback(() => {
    if (mode === 'line') {
      return [lineInputs.x1, lineInputs.y1, lineInputs.x2, lineInputs.y2];
    } else {
      return [circleInputs.xc, circleInputs.yc, circleInputs.r];
    }
  }, [mode, lineInputs, circleInputs]);

  const draw = useCallback(() => {
    const params = getParams();
    let result;

    if (mode === 'circle' && algorithm !== 'bresenhamCircle') {
      alert('Для окружности используется только алгоритм Брезенхема');
      return;
    }

    if (mode === 'line') {
      const [x0, y0, x1, y1] = params;
      result = ALGORITHMS[algorithm](x0, y0, x1, y1);
    } else {
      const [xc, yc, r] = params;
      result = ALGORITHMS['bresenhamCircle'](xc, yc, r);
    }

    setCurrentResult(result);
    setResults([result]);
    setAllResults([result]);
  }, [algorithm, mode, getParams]);

  const clear = useCallback(() => {
    setCurrentResult(null);
    setAllResults([]);
    setResults([]);
    setSelectedPoints([]);
  }, []);

  const compareAll = useCallback(() => {
    const params = getParams();
    const newResults = [];

    if (mode === 'line') {
      LINE_ALGOS.forEach(alg => {
        const [x0, y0, x1, y1] = params;
        const result = ALGORITHMS[alg](x0, y0, x1, y1);
        newResults.push(result);
      });
    } else {
      const [xc, yc, r] = params;
      const result = ALGORITHMS['bresenhamCircle'](xc, yc, r);
      newResults.push(result);
    }

    setAllResults(newResults);
    setResults(newResults);
    if (newResults.length > 0) {
      setCurrentResult(newResults[0]);
    }
  }, [mode, getParams]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Растровые алгоритмы</h1>
        <p>Демонстрация базовых алгоритмов растеризации отрезков и окружностей</p>
      </header>

      <div className="main-content">
        <div className="left-panel">
          <Controls
            algorithm={algorithm}
            onAlgorithmChange={setAlgorithm}
            mode={mode}
            onModeChange={setMode}
            lineInputs={lineInputs}
            circleInputs={circleInputs}
            onInputChange={handleInputChange}
            selectedPoints={selectedPoints}
            onDraw={draw}
            onClear={clear}
            onCompareAll={compareAll}
          />
          <Statistics currentResult={currentResult} allResults={allResults} />
        </div>

        <div className="canvas-container">
          <Canvas
            results={results}
            currentPoints={selectedPoints}
            onPointSelect={handlePointSelect}
            scale={scale}
            onScaleChange={setScale}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
